$(function () {
    'use strict';

    // barChart
    var data = {
        labels: ["Beans", "Coffee", "Non-Coffee", "Snack & Food"],
        datasets: [{
            label: 'sales product',
            data: [5, 19, 15, 8],
            backgroundColor: [
                'rgba(88, 188, 165, 1)',
                'rgba(198, 190, 108, 1)',
                'rgba(255, 105, 73, 1)',
                'rgba(242, 185, 57, 1)',
            ],
            borderColor: [
                'rgba(88, 188, 165, 1)',
                'rgba(198, 190, 108, 1)',
                'rgba(255, 105, 73, 1)',
                'rgba(242, 185, 57, 1)',
            ],
            borderWidth: 1,
            fill: false
        }]
    };
    var options = {
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero: true
                }
            }]
        },
        legend: {
            display: false
        },
        elements: {
            point: {
                radius: 0
            }
        }

    };

    // doughnutChart
    var doughnutPieData = {
        datasets: [{
            data: [5, 19, 15, 8],
            backgroundColor: [
                'rgba(88, 188, 165, 1)',
                'rgba(198, 190, 108, 1)',
                'rgba(255, 105, 73, 1)',
                'rgba(242, 185, 57, 1)',
            ],
            borderColor: [
                'rgba(88, 188, 165, 1)',
                'rgba(198, 190, 108, 1)',
                'rgba(255, 105, 73, 1)',
                'rgba(242, 185, 57, 1)',
            ],
        }],
        labels: [
            'Beans',
            'Coffee',
            'Non-Coffee',
            'Snack & Food'
        ]
    };
    var doughnutPieOptions = {
        responsive: true,
        animation: {
            animateScale: true,
            animateRotate: true
        }
    };

    // areaChart
    var areaData = {
        labels: ["Beans", "Coffee", "Non-Coffee", "Snack & Food"],
        datasets: [{
            label: 'sales product',
            data: [19, 5, 15, 8],
            backgroundColor: [
                'rgba(88, 188, 165, 0.2)',
                'rgba(198, 190, 108, 0.2)',
                'rgba(255, 105, 73, 0.2)',
                'rgba(242, 185, 57, 0.2)',
            ],
            borderColor: [
                'rgba(88, 188, 165, 1)',
                'rgba(198, 190, 108, 1)',
                'rgba(255, 105, 73, 1)',
                'rgba(242, 185, 57, 1)',
            ],
            borderWidth: 1,
            fill: true,
        }]
    };
    var areaOptions = {
        plugins: {
            filler: {
                propagate: true
            }
        }
    }

    if ($("#barChart").length) {
        var barChartCanvas = $("#barChart").get(0).getContext("2d");
        new Chart(barChartCanvas, {
            type: 'bar',
            data: data,
            options: options
        });
    }

    if ($("#doughnutChart").length) {
        var doughnutChartCanvas = $("#doughnutChart").get(0).getContext("2d");
        new Chart(doughnutChartCanvas, {
            type: 'doughnut',
            data: doughnutPieData,
            options: doughnutPieOptions
        });
    }

    if ($("#pieChart").length) {
        var pieChartCanvas = $("#pieChart").get(0).getContext("2d");
        new Chart(pieChartCanvas, {
            type: 'pie',
            data: doughnutPieData,
            options: doughnutPieOptions
        });
    }

    if ($("#areaChart").length) {
        var areaChartCanvas = $("#areaChart").get(0).getContext("2d");
        new Chart(areaChartCanvas, {
            type: 'line',
            data: areaData,
            options: areaOptions
        });
    }
});